const form=document.querySelector(".needs-validation"),inputs=document.querySelectorAll("input"),select=document.getElementById("teams"),alert=document.querySelector(".alert");form.addEventListener("submit",e=>{e.preventDefault(),inputs.forEach(e=>{""===e.value?(form.classList.add("was-validated"),alert.classList.add("d-none")):(form.classList.remove("was-validated"),alert.classList.remove("d-none")),e.value="",select.value=""})});